service.get('/mobile/custom/FIF_IncidentReport_xx/incidents', function(req,res) {
  var payload = {};
  var resource = {};
  if (req.query.contact){
    resource = 'GetIncidentsByCustomer';
    payload = {
      Body: {
        GetIncidentsByCustomer: {
          'UserName': req.query.contact
        }
      }
    };
  }
